create table recipe
(
    id          bigint generated by default as identity
        constraint recipe_pkey
            primary key,
    accepted    boolean,
    calories    integer,
    cuisine     varchar(255),
    image       varchar(255),
    ingredients varchar(2000),
    minutes     integer,
    name        varchar(255),
    recipe      varchar(2000),
    type        varchar(255)
);

alter table recipe
    owner to cdnooowshipnoa;

