INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 274, 'Австрийская кухня', 'photo1.jpg', 'Мука 250, яйцо 1 шт., масло растительное 40, вода кипяченая теплая 60, уксус 2, масло или маргарин для смазывания 10, для начинки: яблоки 1000, изюм 50, миндаль 50, сухари толченые 50, сахар 100, корица 3, пудра сахарная 50, масло сливочное 10, соль 3.', 45, 'Штрудель яблочный', 'В муку вводят яйцо, соль, масло, воду, уксус и приготавливают крутое тесто. Тесто не мешают, а выбивают о доску, пока оно не  перестанет прилипать к рукам и не появятся пузыри. Доску  посыпают мукой, кладут ком теста, слегка смачивают его  тепловатой водо', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 73, 'Американская кухня', 'photo2.jpg', 'Фасоль 80, грудинка копченая 50, масло сливочное 20, лук репчатый 10, томат-пюре 10, зелень петрушки 5.', 15, 'Фасоль по-американски', 'Мелко нарезанный лук пассируют в масле, добавляют томат-пюре и нарезанную соломкой копченую свиную грудинку, все вместе  поджаривают, заливают бульоном и варят, пока грудинка не станет  мягкой, после чего смешивают с отварной фасолью и тушат еще 10- 15 ми', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 141, 'Азербайджанская кухня', 'photo3.jpg', 'На 4 порции. Баранина (почечная часть или мякоть задней ноги) - 600 г. лук репчатый - 2 головки, уксус - 5 ст. л. масло сливочное - 25 г. лук зеленый - 100 г. помидоры свежие - 4 шт. соус ткемали - 3--4 ст. л. барбарис сушеный или свежий - 12-16 шт. лимон', 60, 'Шашлык по-азербайджански', 'Баранину нарезать кусками в форме кубиков (по 30-40 г.), положить в фарфоровую или эмалированную посуду, посолить,  посыпать молотым перцем, добавить мелко нарезанный или натертый  репчатый лук, зелень петрушки, винный уксус или лимонный сок,  перемешать,', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 135, 'Белорусская кухня', '1130091.jpg', 'Картофель 140, шампиньоны 60, лук репчатый 25, капуста квашенная 35, масло растительное 10, уксус (3%) 3, сахар 3.', 15, 'Салат "Минский"', 'К отварному картофелю, нарезанному мелкими ломтиками, добавляют нашинкованные капусту, репчатый лук и вареные шампиньоны.  Заправляют салат уксусом, растительным маслом и сахаром.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 29, 'Бельгийская кухня', 'p_O.jpg', 'Шампиньоны 100, масло сливочное 10, лук репчатый 10, мука 5, бульон 200, смета а 30, яйцо 1/2 шт., зелень петрушки 5, перец молотый, соль.', 10, 'Суп из шампиньонов', 'К отварному картофелю, нарезанному мелкими ломтиками, добавляют нашинкованные капусту, репчатый лук и вареные шампиньоны.  Заправляют салат уксусом, растительным маслом и сахаром.', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 90, 'Грузинская кухня', 'recept_4443_r9s1.jpg', 'Говядина 190, или баранина 180, рис 25, жир животный 10, лук репчатый 30, чеснок 3, томат-пюре 10, ткемали 10, или лаваш кислый 4, хмели-сунели 0,4, специи, перец стручковый, кинза 10, соль.', 90, 'Харчо по-грузински', 'Грудинку (Кость предварительно удаляют) рубят вместе с хрящами кусочками по 25-30 г, заливают холодной водой и варят,  периодически снимая пену. Затем кладут замоченный в воде рис,  пассированный лук, доводят блюдо до готовности, заправляют  соусом ткемал', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 185, 'Датская кухня', 'datskii-salat-iz-vermisheli_1287917984_0.jpg', 'Макароны 80, морковь 30, сельдерей (Корень) 20, капуста цветная 50, ветчина 80, масло растительное 10, уксус 5, майонез 10, горчица 2, соль.', 20, 'Салат датский', 'Макароны отваривают в соленой воде. Морковь и корень сельдерея, нарезаные кубиками, цветную капусту, разобраную на кочешки,  отваривают в подсоленной воде, не давая развариться. Охлажденные  овощи перемешивают с макаронами, заправляют майонезом,  растител', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 126, 'Датская кухня', 'df88fa1f0e79df0352be18935e6e27e1.jpg', 'Курица 200, ананас 200, майонез 20, сливки 20, салат зеленый 10, соль, сахар.', 3, 'Курица соленая с ананасом', 'Воду кипятят с солью и сахаром и охлаждают. Курицу потрошат, вымывают, разделяют на порции и выдерживают в приготовленном  рассоле в теч 2 дней, время от времени переворачивая. Затем  тушку вынимают, отваривают в небольшом количестве воды, снимают  с нее ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 186, 'Египетская кухня', 'p_O_1.jpg', 'Фисташки 60, помидоры 120, лук репчатый 25, перец красный молотый, соль.', 10, 'Салат египетский ', 'Помидоры очищают от кожицы, удаляют семена, мелко нарезают, смешивают с нашинкованным луком, толчеными фисташками, солью и  молотым красным перцем. Смесь должна постоять 10 минут, после  чего салат готов к подаче на стол.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 62, 'Коми кухня', 'salat_iz_chernoi_redki_s_morkoviu-51125.jpg', 'Редька 70, морковь 60, сметана 20, уксус 2, сахар 2, соль.', 15, 'Моркова кушман (Редька тертая с морковью)', 'Редьку и морковь моют, очищают от кожицы, измельчают на крупной терке, солят. Добавив сахар и уксус, заправляют сметаной и  тщательно перемешивают.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 150, 'Индийская кухня', 'unnamed.jpg', 'Фасоль 50, картофель 50, помидоры 25, масло растительное 10, лук 25, чеснок 2, порошок "Карри" 2, лимон 1/6 шт., соль.', 120, 'Салат индийский', 'Нашинкованный лук и чеснок обжаривают в масле до золотистого цвета, всыпают порошок "Карри", выдержав на огне 3 минут, кладут  нарезанные помидоры и отварную фасоль, затем мелко нарезанный  картофель. Перед подачей добавляют лимонный сок и соль.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 202, 'Иорданская кухня', 'sup-pyure-iz-chechevitsy-s-kuritsej-1024x725.jpg', 'Чечевица 100, лук репчатый 50, чеснок 5, мука 10, масло оливковое 10, гренки 30, яйцо 1/2 шт., перец молотый красный и черный, соль.', 60, 'Суп-крем из чечевицы', 'Отваривают чечевицу, добавляют мелко нарезанный репчатый лук, чеснок, доводят все до готовности и протирают через сито.  Пассируют на растительном масле муку, вводят красный и черный  молотый перец, разбавляют муку бульоном или чечевичным отваром и  соеди', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 204, 'Итальянская кухня ', 'unnamed_1.jpg', 'Спагетти 60, масло оливковое 20, лук репчатый 10, чеснок 2, помидоры 20, сыр 20, соль.', 40, 'Спагетти "Ницца"', 'Отваривают спагетти. Пассируют на сливочном масле мелко нарезанный лук и чеснок, добавляют мелко нарезанные помидоры,  подрумянивают их вместе с луком и вводят спагетти, осторожно  перемешивая их. Отдельно подают тертый сыр.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 300, 'Итальянская кухня ', 'p_O_2.jpg', 'На 4 порции: 2 кг помидоров, 200 г репчатого лука 50 г копченого шпика, 2 бульонных кубика 1 пучок базилика, 125 мл., молока, соль 65 г муки, 1 яйцо', 15, 'Суп из помидоров с клецками из базилика', 'Снять с помидоров кожуру, крупно нарезать. Репчатый лук очистить и мелко порубить. Шпик нарезать кубиками. Потушить, пока они не  станут прозрачными. Добавить помидоры и бульонные кубики, тушить  приблизительно 15 мин.  Несколько листочков отложить, остав', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 72, 'Итальянская кухня ', '15666548371.jpg', 'Ветчина жирная 20, ветчина постная 20, лук репчатый 20, морковь 20, капуста белокочанная 30, картофель 30, помидоры свежие 50, горох 10, фасоль 25, чеснок 3, рис 10, сыр 20, базилик 2, лавр, соль.', 80, 'Суп "Минестрон"', 'Натирают на терке половину полагающейся по рецепту жирной ветчины, постную ветчину нарезают кубиками и оба продукта  поджаривают до бледно-желтого цвета с тонко нарезанным луком.  Овощи мелко нарезают, добавляют базилик и лавр и обжаривают до  желтизны. В', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 200, 'Итальянская кухня ', 'Acquacotta_cr.jpg', 'Крошки белого хлеба 20, сыр "Пармезан" или иной 210, яйцо 1/2 шт., бульон 400, соль.', 10, 'Суп с хлебом по-итальянски', 'Хлебные крошки хорошо перемешивают с тертым сыром и взбитым яйцом, солят, перчат, приправляют мускатным орехом и вводят эту  смесь при непрерывном помешивании в бульон. Кастрюлю накрывают  крышкой и ставят на слабый огонь на 7-8 мин. Перед подачей  помеши', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 183, 'Казахская кухня', 'b2a00a2b.jpg', 'Мука 100, масло сливочное 20, сметана 30, сахар 5, яйцо 1/4 шт., баранина 110, курица 100, рис 15, лук репчатый 20, перец, соль.', 60, 'Балиш (пирог)', 'Мякоть баранины, нарезанную кубиками, соединяют с сырым рисом, луком, солью, перцем и водой. На сметане с маслом, добавив соль  и сахар, замешивают сдобное тесто и раскатывают его в два пласта  толщиной 1 см. Один пласт укладывают на противень, кладут ров', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 153, 'Казахская кухня', 'https___img.anews.com_media_posts_images_20181113_100681692.jpg', 'Говядина 90, сало растительное 2, яйцо 1 шт., картофель 50, морковь 15, огурцы соленые 50, горошек зеленый 10, яблоки свежие 35, майонез 40, сметана 15, перец, зелень.', 40, 'Салат по-казахски ', 'Жареное мясо, вареные картофель и морковь, соленые огурцы, очищенные от кожицы, и свежие яблоки нарезают тонкими ломтиками,  добавляют зеленый горошек и перемешивают. Заправив салат перцем,  сметаной и майонезом, выкладывают его в салатницу, украшают  кус', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 176, 'Карельская кухня', 'salat_quotdushechkaquot-664603.jpg', 'Икра свежая 75, молоки 30, печень рыбы 30, лук зеленый или репчатый 25.', 30, 'Салат "Карельский"', 'Икру свежей рыбы солят, а молоки и печень варят. Затем икру, молоки, печень и лук рубят, солят и перемешивают.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 268, 'Киргизская кухня', 'pilita-sladkoe-izdelie-iz-testa-e1576154146667.jpg', 'Мука пшеничная 80, масло хлопковое 5, сода 0,5, дрожжи 3', 50, 'Женмомо (Изделие из теста)', 'Кислое тесто соединяют с пресным тестом в пропорции 1: 1, натирают питьевой содой, затем добавляют растительное масло,  несколько раз обваливают ком в муке и натирают содой,  разделывают мелкие шарики и отваривают их на пару в течение 40- 50 мин. Подают к', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 389, 'Киргизская кухня', 'b0a987b6.jpg', 'Мука пшеничная 80, баранина 80, лук репчатый 50, жир бараний топленый 3, перец молотый красный 0,5.', 30, 'Самса (Изделие из теста)', 'Фарш из нарубленого кусочками сырого мяса, рубленого сырого репчатого лука и перца кладут на лепешки из пресного теста,  формуют пирожки и выпекают их в специальной печи (Тандыре).', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 54, 'Киргизская кухня', 'shorpo-409463.jpg', 'Баранина 170, вода 300, картофель 170, помидоры 50, лук репчатый 20, сало баранье (Сырец) 30, перец 0,5, зелень, соль.', 30, 'Шорпо (Суп)', 'Баранину рубят кусочками с костью, посыпают солью и перцем, кладут в котел с жиром и жарят до образования на поверхности  поджаристой корочки, добавляют репчатый лук, нарезанный  кольцами, и свежие помидоры, затем вливают воду, дают прокипеть  в течение 5', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 121, 'Китайская кухня', 'scale_1200.jpg', 'Рыба 200, крахмал 20, яйцо 1/4 шт., сало свиное 30, соус соевый 5, водка 10, лук репчатый 10, лук зеленый 10, имбирь 10, сахар 20, уксус 10, бульон 20, соль.', 40, 'Рыба, жареная в кисло-сладком соусе', 'Филе судака нарезают брусочками, смачивают в смеси белка и крахмала, разведенного холодной водой (1: 1), жарят во фритюре и  откидывают. Одновременно кипятят смесь из водки, уксуса,  крахмала, разведенного холодной водой (1: 2), не заправленного  бульона,', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 71, 'Китайская кухня', '2-perets-sladkij-polza-i-vred-3.jpg', 'Капуста белокочанная 180, морковь 40, перец стручковый красный сушеный, имбирь 1, перец душистый 7, масло кунжутное 20, сахар 10, уксус 5.', 10, 'Салат из овощей с перцем', 'Белокочанную капусту нарезают шашками, сырую морковь - кружочками или соломкой, красный стручковый перец -  квадратиками. Подготовленную капусту варят вместе с морковью до  полуготовности, а затем откидывают, тщательно отжимают воду,  заправляют солью, са', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 148, 'Китайская кухня', 'egg.jpg', 'Яйца 2шт., лук репчатый 10, соус соевый 30 (25 подается отдельно), водка 10, петрушка 10, сахар 20, укроп 10, бадьян, перец душистый, корица, гвоздика, соль.', 50, 'Яйца маринованные', 'Куриные или утиные яйца варят 10-12 минут, опускают в холодную воду, очищают от скорлупы, накалывают в нескольких местах и  снова варят 10-15 минут в воде с добавлением соевого соуса,  водки, бадьяна, душистого перца, корицы, гвоздики, имбиря,  петрушки, ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 28, 'Кубинская кухня', 'creamy-roasted-cauliflower-soup-recipe-1-1_1571817213-e1571817255211-630x315.jpg', 'Капуста цветная 90, яйцо 1/2 шт., мука пшеничная 10, сыр тертый 5, бульон мясной (Или овощной отвар) 300, соль.', 30, 'Суп из цветной капусты', 'Цветную капусту тщательно промывают, заливают крутым подсоленным кипятком (Так, чтобы кипяток только покрыл капусту), варят около  30 минут на слабом огне и откидывают на сито. Сырые яичные  желтки немного прогревают, добавляют муку и тертый сыр, солят и ', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 269, 'Литовская кухня', '9e28768922cfb95d37c1e20312703be80c3b708eb2878ca02481b79f8cc14b21.jpg', 'Картофель 290, яйцо 1/4 шт., жир свиной 20, сметана 20, соль 1,5.', 20, 'Оладьи картофельные по Литовски', 'Тертый сырой картофель соединяют с яйцами, солью и перемешивают. Из полученной массы формуют оладьи и жарят их в свином жире. При  подаче поливают сметаной.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 248, 'Кухня стран Центральной Америки', '1.jpg', 'Цыпленок 350, лук репчатый 25, лимон 1/4 шт., чеснок 3, помидоры 80, лук зеленый 5, зелень петрушки 5, перец маринованный 10, масло сливочное или маргарин 30, перец молотый черный, мука 5, соль.', 180, 'Жаркое "Варадо де франчо" (Коста Рика)', 'Цыпленка разделывают на восемь частей и в течение 2 часов маринуют, смазав смесью из лимонного сока, очень мелко  нарезанного лука, молотого перца и растертого с солью чеснока.  Затем обжаривают мясо в кипящем масле до золотистого цвета. Сняв  с помидоров', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 219, 'Кухня стран Юго-Восточной Азии', 'depositphotos_218642584-stock-photo-asian-food-bami-goreng-noodles.jpg', 'Орехи 60, лук репчатый 50, чеснок 2, масло растительное 25, мясо свиное 60, помидоры 180, грибы 20, стебли чеснока 5, перец красный маринованный 20, зелень петрушки 3, яйцо 1/2 шт., лимон 1/4 шт., перец, соль.', 60, 'Бами (индонезия)', 'Из яиц, соли, воды готовят массу для омлета и дают ей постоять. Отваривают в течение 10 минут в крутом кипятке орехи. На  сковороду с разогретым маслом кладут нашинкованный лук и чеснок,  нарезанное кусочками мясо, очищенные от кожицы и измельченные  поми', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 93, 'Китайская кухня', 'p_O_6.jpg', 'Помидоры свежие 150, сахар-песок 30.', 20, 'Салат из помидоров китайский', 'Помидоры нарезают кружочками толщиной 3 мм, кладут горкой в салатник и посыпают сахаром.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 117, 'Кухня стран Юго-Восточной Азии', 'unnamed_2.jpg', 'Барабулька 200, помидоры 100, чеснок 3, вино белое сухое 10, масло растительное 20, шафран 0,2, зелень петрушки 5, перец по вкусу, соль.', 40, 'Барабулька по-восточному (Камбоджа)', 'Рыбу очищают, промывают, подсушивают, панируют в муке и жарят с обеих сторон на растительном масле. Помидоры очищают, разрезают  пополам, вынимают семена, нарезают кубиками, выдерживают в  растительном масле, добавляют толченый чеснок, рубленую  петрушку,', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 191, 'Кухня стран Юго-Восточной Азии', 'zate_bliudo-446776.jpg', 'Мясо 100, лук репчатый 30, чеснок 2, орехи земляные 5, масло растительное 5, перец молотый черный, соль.', 150, 'Зате (индонезия)', 'Курятину или молодую свинину нарезают небольшими кубиками и натирают смесью из нарубленого лука, растертого чеснока,  очищенных, слегка поджаренных орехов и растительного масла.  Выдерживают 1-2 часа, затем перчат, солят и обжаривают на  вертеле. Подают с', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 153, 'Кухня стран Юго-Восточной Азии', '1549506621_0_108_1920_1188_1400x0_80_0_0_405a3cf3ebe9a40ab6259dde92587a67.jpg', 'Свинина 100, масло оливковое 20, лук-порей 30, сельдерей (Корень) 20, молоко кокосовое 10, уксус 5, мускатный орех, перец молотый черный, соль.', 60, 'Суп из свинины (Таиланд)', 'Нарезанную небольшими кусочками свинину отваривают в подсоленной воде, добавляют оливковое масло и тушат мясо, пока жидкость не  испарится. Отдельно отваривают в подсоленной воде белую часть  мелко нарезанного лука-порея и измельченный на терке корень  се', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 213, 'Латышская кухня', 'kotlety-iz-kurinogo-file-rublennye-s-maionezom-i-kraxmalom_1556862384_9_max.jpg', 'Говядина 160, корейка копченая 30, лук репчатый 20, мука пшеничная 5, сметана 30, картофель 200, огурцы соленые 50, соль.', 60, 'Мясо рубленое', 'Корейку, нарубленную мелкими кубиками, обжаривают с луком и добавляют мясо, пропущенное через мясорубку с крупной решеткой.  Продолжая жаренье, посыпают мясо мукой, добавляют бульон,  сметану, специи и доводят до готовности. Подают с отварным  картофелем ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 133, 'Латышская кухня', '9e28768922cfb95d37c1e20312703be80c3b708eb2878ca02481b79f8cc14b21.jpg', 'Картофель 150, мука пшеничная 20, яйцо 1/2 шт., орех мускатный 0,2, масло растительное 5, сметана 30 или масло сливочное 10, соль.', 40, 'Оладьи картофельные со сметаной', 'Сырой картофель измельчают на терке, соединяют с мукой, сырыми яйцами, мускатным орехом, массу перемешивают и жарят оладьи.  Подают, полив сметаной или маслом.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 164, 'Украинская кухня ', 'privately.ru_5-receptov-samyh-vkusnyh-varenikov-klassicheskie-sytnye-sladkie-i-dazhe-2.jpg', '0,5 стакана муки, 600 г творога, 4 яичных белка, 30 г сливочного масла, 1,5 ст. ложки сахарного песка.', 15, 'Галушки творожные', 'Творог пропустить через мясорубку, добавить масло, муку, сахар, соль, взбитые белки и хорошенько растереть. Полученное  тесто раскатать в жгут толщиной 1,5 см и нарезать галушки  шириной 1,5 см. Варить в подсоленной воде 15 минут. Подать со  сметаной.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 178, 'Укаринская кухня', '454882d9d56cbd49fa39c0b5b8243794.jpeg', '3 стакана пшеничной муки, 2 яйца, 100 г сливочного масла, 0,3 стакана воды, соль.', 20, 'Галушки сдобные', 'В предварительно просеянной муке сделать углубление, влить в него воду, добавить сливочное масло, взбитые с солью яйца и  замесить тесто круче, чем для вареников. Тесто закрыть на 15- 20 минут полотенцем, затем раскатать в пласт толщиной 1 см.  Нарезать г', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 328, 'Киргизкая кухня', 'scale_1100.jpg', '500 г баранины, 50 г курдючного сала, 4 луковицы, 2 ст. ложки топленого масла, 0,5 стакана 9%-ного уксуса, перец, соль. Для теста: 3 стакана пшеничной муки, 10 г дрожжей, соль.', 40, 'Хошан ', 'Муку разделить на 2 части: из одной замесить дрожжевое тесто, из другой - пресное. Когда кислое тесто подойдет, смешать его  с пресным, разделить на кусочки по 40-50 г, раскатать  лепешки. Мякоть баранины или курдючное сало порубить или пропуститъ через м', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 79, 'Русская кухня', '259007_original.jpg', 'Капуста тушеная 220, говядина 75 или баранина, или козлятина 75, или свинина 70, сосиски 25, сардельки 25, окорок копчено-вареный 20, почки говяжьи 60 или язык говяжий 40, огурцы соленые 50, каперсы 30, маслины 10, оливки 10, соус красный 55, сухари 4, сы', 80, 'Солянка сборная мясная на сковороде', 'Вареные сосиски, сардельки, ветчину, почки, язык, мясо нарезают тонкими небольшими ломтиками, слегка обжаривают, добавляют  очищенные от кожицы и семян соленые огурцы нарезанные дольками,  оливки, маслины, каперсы, красный соус и нагревают до кипения.  На', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 225, 'Сирийская кухня', 'saffron-rice-chicken-135575040-572a290b5f9b58c34c3c3c54.jpg', 'Цыпленок 300, рис 70, лук репчатый 80, масло сливочное 30, помидоры 80, гвоздика, перец молотый черный, соль.', 50, 'Цыпленок с рисом', 'Выпотрошенную и промытую тушку цыпленка запекают, предварительно полив ее сливочным маслом. Нарезают тушку на куски и вынимают  кости. Поджаривают на сливочном масле измельченный на терке  репчатый лук и рис. Наливают воду, добавляют соль, молотый  черный', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 87, 'Таджикская кухня', '950e0563f0c046a6b0dd57bd6d29ab30.jpg', 'Баранина 300, масло топленое 25, мука пшеничная 15, лук репчатый 65, гранаты 35, зелень 15, специи, соль.', 40, 'Кабобы "Рохат" (колбаски)', 'Баранину вместе с репчатым луком (1/3 нормы) дважды пропускают через мясорубку, добавляют соль, перец и разделывают в виде  кабобов (Колбасок) овально-удлиненной формы. Затем панируют их в  муке и слегка обжаривают, репчатый лук, нарезанный кольцами,  пас', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 211, 'Таджикская кухня', '1034167435.jpg', 'Баранина 150, картофель 200, лук репчатый 60, морковь 40, помидоры свежие 75, жир 15, зелень 5, перец болгарский 20, специи, соль.', 30, 'Кавурдок (жаркое)', 'Баранину (Грудинку, корейку, лопатку) рубят кусочками по 40-50 г, обжаривают до образования румяной корочки, добавляют  нарезанный соломкой лук, морковь (Дольками), помидоры и  обжаривают все вместе, затем заливают водой, тушат под крышкой  до готовности ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 235, 'Татарская кухня', 'echpochmaks.jpg', 'Тесто 90, мясо 70, картофель 80, масло топленое 15, лук репчатый 20, яйцо 1/8 шт., перец, соль, бульон.', 20, 'Эчпочмак', 'Жирное мясо и картофель нарезают мелкими кубиками и смешивают с мелко шинкованным репчатым луком, маслом, перцем и солью.  Начинку следует готовить не всю сразу, небольшими порциями.  Дрожжевое или пресное тесто раскатывают в лепешки величиной с  чайное б', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 322, 'Тувинская кухня', 'mabdb640.jpg', 'Мука 35, яйцо 1/4 шт., вода 10, баранина (Задняя и лопаточная часть) 100, лук репчатый 25, масло топленое 15, соль.', 30, 'Лапша тувинская', 'В кипящий процеженный бульон из бараньих косточек кладут баранину, нарезанную мелкими кусочками. Суп варят, пока мясо не  станет мягким, и солят. Из муки, топленого масла, яиц и соли  замешивают крутое тесто, раскатывают его в пласт и нарезают  лапшу длин', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 148, 'Тувинская кухня', 'p_O_3.jpg', 'Мука 80, яйцо 2/5 шт., вода 30, баранина 140, лук 15, специи, соль.', 40, 'Манчи', 'Из муки, воды, яиц и соли замешивают крутое тесто, раскатывают лепешки. Готовят фарш: баранину вместе с луком пропускают через  мясорубку, добавляют воду, соль, перец и массу выбивают. На  середину каждой лепешки кладут фарш, края теста защипывают,  прида', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 254, 'Турецкая кухня', 'scale_11100 .jpg', 'Курица 200, морковь 20, петрушка 20, сельдерей (Корень) 10, лук репчатый 20, перец стручковый маринованный 5, масло сливочное 25, рис 60, изюм 10, миндаль 10, перец черный молотый, соль.', 80, 'Плов из курицы по-турецки', 'Курицу варят в подсоленной воде с кореньями и луком до готовности. Промытый и высушенный рис поджаривают в масле до  прозрачности и заливают бульоном. Заправляют солью, перцем,  добавляют изюм, дают бурно прокипеть 10 минут, затем закрывают  кастрюлю и ст', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 59, 'Узбекская кухня', 'uzbek-manties.jpg', 'На 4 порции. Баранина - 440 г. сало баранье - 45 г. лук репчатый, очищенный - 500 г. мука в/с (- 320 г. вода - 20 г.', 45, 'Манты (3 штуки в порции) по-узбекски', 'Баранину и лук мелко рубят, заправляют солью и перцем. Из крутого теста раскатывают тонкие лепешки (края лепешки должны  быть раскатаны тоньше, чем середина). На середину лепешки кладут  фарш, сверху фарша кладут кусочек сала и защипывают края,  придавая ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 234, 'Узбекская кухня', 'recept_6178_dbv9.jpg', 'На 4 порции. Баранина - 280 г. сало баранье - 25 г. лук репчатый - 100 г. зелень петрушки - 40 г. мука пшеничная - 15 г. Маринад: анис - 2 г. лук репчатый - 40 г. перец красный - 2 г. уксус 3%-ный - 20 г.', 60, 'Шашлык по-узбекски', 'Баранину нарезают на мелкие кусочки и вместе с нашинкованным луком заливают маринадом, перемешивают и оставляют на 3-4 часа. Затем мясо нанизывают на шпажку, на конец которой насаживают  кусок сала, посылают мукой и обжаривают над раскаленными углями. Под', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 44, 'Украинская кухня', 'bb647ec7.jpg', 'Говядина 54, кости 100, капуста свежая 75 картофель 100, морковь 25, корень петрушки 10, лук репчатый 20, чеснок 2, томат-пюре 20 или помидоры свежие 75, перец болгарский 10, мука пшеничная 3, жир 5, сало (Шпик) 5, сметана 20, сахар 5, уксус (3%), 8, зеле', 30, 'Борщ украинский с мясом', 'В кипящий костный бульон кладут мясо, варят его до готовности, вынимают и нарезают на порции. Свеклу шинкуют соломкой, солят,  сбрызгивают уксусом, перемешивают и тушат с добавлением жира из  мясного бульона, томата-пюре, сахара до полуготовности. Коренья', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 245, 'Украинская кухня', 'po-kievsky-kotlety-601x601.jpg', 'Курятина 200, масло сливочное 30, яйцо 1/4 шт., сухари пшеничные 20, жир для жаренья 15, картофель 350, горошек зеленый 50, соль.', 30, 'Котлеты по-киевски', 'Куриное филе отбивают, фаршируют, придавая ему форму груши, сливочным маслом, смачивают в яйце, панируют в сухарях,  повторяют эту операцию дважды, и жарят во фритюре. При подаче  поливают растопленным маслом. Котлеты можно подать на гренках из  белого хл', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 135, 'Финская кухня', 'finskij-salat-rosoli-600x400.jpg', 'Свекла 100, ананас 100, сливки взбитые 50.', 20, 'Салат из свеклы по фински', 'Отварную свеклу, нарезанную кубиками, и ананас, нарезанный ломтиками, смешивают и заправляют взбитыми сливками.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 123, 'Финская кухня', '48455a3965c14d166ab49527d1c29134.jpg', 'Сельдь 60, говядина 50, картофель 50, свекла маринованная 30, огурцы соленые 20, лук репчатый 20, яйцо 1/2 шт., сливки 20, зелень петрушки 3, перец молотый черный.', 15, 'Салат селедочный по фински', 'Селедку очищают, вымачивают, освобождают от костей и нарезают мелкими кусочками. Холодный отварной картофель, маринованную  свеклу, соленые огурцы, репчатый лук и яблоки нарезают кубиками.  Все это смешивают с мелко нарезанными кусочками мяса, добавляют  ', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 125, 'Финская кухня', 'rubber-cutlets.jpg', 'Фарш говяжий 60, фарш телячий 30, фарш бараний 30, сметана 20, вода 20, крошка сухарная 20, желток 1/2 шт., пюре картофельное 150.', 30, 'Чаттбуллар (Мясные котлеты)', 'Говяжий, телячий и бараний фарш, сухарную крошку, яичные желтки, сметану и воду смешивают. Из полученной вязкой массы формуют  небольшие шарики и поджаривают их на сливочном масле, встряхивая  сковороду так, чтобы шарики поджаривались равномерно. На гарни', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 227, 'Французская кухня', 'ryba-testo.jpg', 'Рыба 150, мука пшеничная 100, яйцо 1 шт., пиво 100, масло сливочное 10, масло растительное рафинированное 20, лук зеленый 5, перец душистый горошком 1, соль.', 30, 'Рыба в тесте', 'Муку соединяют с солью, сырыми яичными желтками и пивом, добавляют растопленное сливочное масло и вымешивают тесто.  Яичный белок, взбитый до образования пену, вводят в тесто.  Отварную или припущенную рыбу режут на кусочки, посыпают ме ко  нарезанным зел', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 220, 'Французская кухня', '20-minute-Easy-Apple-Arugula-Salad-with-Pecans-and-Lemon-Vinaigrette.-A-healthy-hearty-side-dish-for-fall-and-winter.-salad-recipe-vegan-healthy-dinner-meal-minimalistbaker-1200x630.jpg', 'Горчица готовая 5, уксус 5, масло оливковое 10, сельдерей 30, зелень сельдерея 5, ветчина 10, грибы маринованные 10, яблоки 20, майонез 15, картофель 30, свекла 30, зелень петрушки 5.', 70, 'Салат бакер', 'Горчицу, уксус, оливковое масло взбивают и заливают полученной заправкой мелко нарезанную зелень и корни сельдерея. Выдерживают  один час, а затем добавляют нарезанные ломтиками ветчину,  маринованные грибы, яблоки и осторожно перемешивают с майонезом.  С', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 85, 'Французская кухня', 'b7343f01.jpg', 'Лук репчатый 120, масло сливочное 10, мука пшеничная 5, бульон 250, хлеб белый 20, лавр, перец молотый черный, соль.', 30, 'Суп луковый по-парижски', 'Мелко нарезанный репчатый лук обжаривают в сотейнике на сливочном масле до коричневого цвета, добавляют в него,  размешивая, муку, а затем мясной бульон, лавр, молотый черный  перец и варят на слабом 30 мин. Вынимают лавр и солят по вкусу.  Подсушивают на', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 180, 'Французская кухня', 'scale_1201.jpg', 'Цыпленок 80, бульон 250, миндаль 5, хлеб белый 25.', 30, 'Суп по-королевски', 'Мясо только что поджаренного цыпленка отделяют от костей, кладут в кипящий бульон и варят на слабом огне 15-20 мин. Затем мясо  вынимают, охлаждают и пропускают через мясорубку вместе с  миндалем и замоченным в бульоне белым хлебом. Добавив несколько  лож', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 46, 'Чехословацкая кухня', 'scale_120022.jpg', 'Вода 300, говядина 80, лук репчатый 20, сельдерей (Корень) 10, жир костный или говяжий 5, зелень петрушки 5, соль.', 30, 'Суп из молотой говядины', 'Пропущенную через мясорубку говядину заливают холодной водой и варят 30 мин. Затем кладут нарезанные спассированные коренья,  соль и варят еще 5-10 мин. В готовый суп кладут мелко нарезанную  зелень петрушки.', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 198, 'Шведская кухня', 'KCmLoja2Ask.jpg', 'Пиво 500, сахар 10, мука 10, яйцо 1/2 шт., молоко 10, желток 1/2 шт., корка лимона или имбирь, соль.', 40, 'Елебрад (Суп из пива)', 'В пиво кладут лимонную корку и доводят его до кипения. Муку разводят в холод ом молоке, вливают болтушку в пиво и  приправляют суп сахаром и солью. Яйцо смешивают с мукой,  добавляют немного молока: разболтав, вливают эту жидкость в суп.  Кипятят в течени', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 236, 'Шведская кухня', '6066-600x372.jpg', 'Курица 220, масло сливочное 20, лук репчатый 30, мука 10, сметана или молоко кислое 100, рис 60, салат 10, соль.', 60, 'Курица по-шведски', 'Тушку курицы солят изнутри и снаружи, кладут в кастрюлю с маслом, ставят в жарочный шкаф, румянят со всех сторон и  вынимают. В это же масло добавляют нарезанный ломтиками,  прокипяченный и отцеженный лук, пассируют его, вводят муку,  также пассируют, зат', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 217, 'Швейцарская кухня', 'scale_12002.jpg', 'Картофель 200, шпик копченый 50, сливки 30, молоко 30, орех мускатный, соль.', 30, 'Поджарка картофельная по-швейцарски', 'Сваренный "В мундире" картофель охлаждают, очищают, стругают на терке соломкой. Сало (Шпик), нарезанное мелкими кусочками,  вытапливают на слабом огне. Всыпают в сковороду картофель,  хорошо перемешивают с салом, заправляют солью и мускатным  орехом, влив', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 229, 'Швейцарская кухня', 'fond-3.jpg', 'Сыр швейцарский 120, чеснок 2, вино натуральное белое 120, крахмал 5, вишневка 10, перец молотый черный, орех мускатный, соль.', 20, 'Фондю (Блюдо из сыра)', 'Вино вливают в глиняный горшок, натертый изнутри чесноком, медленно нагревают. Тертый сыр растапливают в вине при  непрерывном помешивании (Мешать лучше не кругами, а  восьмерками). Увеличивают огонь и дают жидкости закипеть.  Добавляют разведенный в воде', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 175, 'Югославская кухня', '1327889048_zapechennye-cyplyata-v-granatovoy-glazuri-farshirovannye-risom.jpg', 'Цыпленок 220, масло растительное 50, петрушка 5, чеснок 3, лук репчатый 20, сок лимонный 10, перец молотый черный, соль.', 40, 'Цыплята по-югославски', 'Цыпленка разрезают на части и каждый кусок накалывают вилкой. Из растительного масла, измельченной свежей петрушки, соли, черного  молотого перца, натертого чеснока, мелко нарезанной луковицы и  лимонного сока готовят маринад. Птицу кладут на 3-4 часа в  ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 141, 'Польская кухня', 'b4a2b58f.jpg', 'Рыба 200, сухари толченые 10, молоко 25, яйцо 1/4 шт., перец молотый черный, лук репчатый 10, соль.', 40, 'Щука фаршированная печеная', 'Подготовленную щуку подсушивают, надрезают кожу вокруг головы, острым ножом отделяют ее от мяса и снимают. Готовят рыбную  массу. Кожу не слишком плотно заполняют фаршем, зашивают. Рыбу  укладывают на противень с разогретым жиром, ставят в нагретый  жароч', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 118, 'Польская кухня', 'IMG_2842.jpg', 'Мозги телячьи 100, масло сливочное 10, лук репчатый 10, уксус 5, яйцо 1/2 шт., зелень петрушки 5, перец молотый черный, соль.', 30, 'Мозги телячьи', 'Мозги обмывают, очищают от пленок, кладут в кипящую подсоленную воду с уксусом и варят 5 мин. Осторожно вынимают, подсушивают,  охлаждают. Лук нарезают мелкими кубиками и жарят в масле до  светло-золотистого цвета. Мозги нарезают крупными кубиками,  соеди', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 152, 'Польская кухня', '2_1570117084-e1570117109648-630x315.jpg', 'Тыква 120, колбаса докторская или чайная 50, яйцо 1 шт., мука 20, масло сливочное 15, соль.', 40, 'Оладушки из тыквы с колбасой', 'Тыкву очищают от кожицы и семян и измельчают на крупной терке. Колбасу, сняв оболочку, мелко рубят или пропускают через  мясорубку. Сырые желтки, растертые с жиром, соединяют с тыквой и  колбасой, вводят муку и соль, размешивают. Ложкой выкладывают  оладу', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 59, 'Китайская кухня', '7b82e75b25399c33120c70fbeaedc854.jpg', 'Редька 180, лук зеленый 10, масло кунжутное 20.', 15, 'Салат из редьки с луком', 'Редьку нарезают соломкой, смешивают с нарезанным зеленым луком, добавляют соль и кунжутное масло. Готовый салат кладут в  салатник. Иногда отдельно подают горячую жидкую рисовую кашу  (Дамичжоу), которую варят обычным способом.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 51, 'Монгольская кухня', 'recipe_3b3b13e5-74c6-491b-b4fc-f851dd905372_large.jpg', 'Бульон мясной 250, горох 60, сок томатный 50, масло сливочное 5, лук репчатый 20, сметана 15, перец, соль.', 40, 'Суп гороховый по-монгольски', 'Мелко шинкованный лук пассируют в масле до золотистого цвета, заливают холодным бульоном, кладут замоченный горох и варят.  Затем лук и горох протирают через сито, кладут в бульон,  добавляют томатный сок, заправляют суп солью и перцем и  проваривают 10 м', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 129, 'Монгольская кухня', 'unnamed5.jpg', 'Голова молодого барашка 300, лук репчатый 50, морковь 50, сельдерей (Корень) 30, рис 50, масло сливочное 10, молоко кислое 50, яйцо 1/4 шт., зелень петрушки и мяты 5, перец молотый черный, кислота лимонная, соль.', 40, 'Суп из головы молодого барашка', 'Голову молодого барашка хорошо промывают, кладут в кастрюлю, заливают холодной водой и варят. Доведя до кипения, удаляют  пену, солят, кладут морковь, репчатый лук, корень сельдерея и  продолжают варить на слабом огне. Когда мясо начнет отделяться  от кос', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 153, 'Молдавская кухня', 'zama-of-fish-800x445.jpg', 'Осетрина 130, или белуга 100, лук репчатый 25, петрушка (Корень) 25, морковь 25, масло сливочное 10, картофель 200, квас 100, зелень петрушки 5, специи, соль.', 45, 'Суп из рыбы по-молдавски', 'Отваривают порционные куски рыбы с кожей (Без хрящей). В полученный бульон закладывают картофель, нарезанный дольками.  Через 15-20 минут - пассированный лук, морковь и петрушку,  нарезанные соломкой: доведя суп до кипения, добавляют  прокипяченный квас, ', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 140, 'Молдавская кухня', '472818fd666947919a7ec59d18694ee8-quality_100Xresize_crop_1Xallow_enlarge_0Xw_680Xh_510.jpg', 'Курица 100, картофель 200, морковь 25, лук репчатый 25, петрушка 15, маргарин столовый 10, кислота лимонная 1, сметана 15, специи, соль.', 40, 'Суп по-молдавски', 'Морковь, лук, петрушку шинкуют соломкой и пассируют. В курином бульоне отваривают до полуготовности картофель, нарезанный  чесночком, затем вводят пассированные коренья, красный перец,  соль. За 5 минут до готовности суп заправляют лимонной кислотой  и ла', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 124, 'Норвежская кухня', 'f7ace77be765001ff8f32f156f4c872e-quality_100Xresize_crop_1Xallow_enlarge_0Xw_680Xh_510.jpg', 'Кольраби 20, бульон 250, грибы свежие 20, сливки 20, желток яичный 1/2 шт., масло сливочное 5.', 35, 'Суп-пюре по-норвежски', 'Из кольраби ранних сортов делают пюре, разводят его бульоном, кладут в суп мелко нарезанные отварные белые грибы, заправляют  сливками, яичными желтками и сливочным маслом.', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 214, 'Молдавская кухня', 'prigotovlenie-blyud-iz-myasa.jpg', 'Баранина 110, жир бараний 15, мука пшеничная 5, лук репчатый 40, томат-пюре 10, чеснок 1, гарнир 150, соль.', 40, 'Кавурма из молодого барашка', 'Мякоть баранины нарезают кусками весом по 25-30 г и обжаривают, добавив муку и томат-пюре, жарят еще 5 минут, затем вливают  немного воды, кладут пассированный лук, соль, чеснок и тушат под  крышкой до готовности. Гарнир из овощей, круп или макаронных  из', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 267, 'Марокканская кухня', 'IMG_5874.jpg', 'Баранина 250, масло растительное 25, чеснок 5, лук репчатый 50, помидоры.', 60, 'Гайин эль гхальми (Гуляш из баранины)', 'Лопатку или бараний окорок нарезают небольшими кусочками, как для гуляша. В глубокой тяжелой кастрюле (Гусятнице) разогревают  растительное масло и обжаривают в нем мясо. Добавляют растертый  чеснок, мелко нарезанный лук, соль, черный молотый перец, лавр,', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 115, 'Марокканская кухня', 'dsc4899-2.jpg', 'Изюм 50, сухари хлебные 150, вода 500, мед 50.', 20, 'Ебаба (Хлебный суп)', 'Изюм варят в воде, всыпают хлебные сухари и заправляют медом. Подается горячим или холодным.', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 34, 'Марийская кухня', 'inx960x640.jpg', 'Творог пастеризованный обезжиренный 300, вода или молоко 700, соль 10.', 15, 'Турисмо торык вуд (Напиток творожный)', 'Сухой творог растирают, вливая тонкой струей кипяченую холодную воду или молоко, до консистенции сливок, присаливают и  настаивают напиток в неокисляющейся посуде.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 187, 'Марийская кухня', '5H2A6652.jpg', 'Стерлядь 110, картофель 10, огурцы свежие или соленые 90, яйцо 1/2 шт., яблоки 20, клюква 15, соус 50.', 30, 'Шолтымо стерлядь (Салат из стерляди)', 'Картофель, отваренный в мундире, и огурцы нарезают мелкими ломтиками, яблоки и сваренные вкрутую яйца - дольками. Стерлядь  припускают с солью и специями, затем охлаждают и нарезают  брусочками. Продукты перемешивают и добавляют клюкву. К салату  подают с', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 85, 'Литовская кухня', 'ukr_borsh.jpg', 'Грудинка или корейка копченая 17, бульон 100, свекла квашеная 65, рассол свекольный 85, лук репчатый 10, морковь 10, петрушка (Корень) 4, сало свиное 6, сахар 2, картофель отварной 100, сметана 15, перец душистый, лавр, соль.', 35, 'Борщ с грудинкой или корейкой', 'В бульон, сваренный на копченой корейке или грудинке, добавляют пассированные морковь, петрушку, специи, сахар, продолжая  варить, через 10 минут кладут нарезанную брусочками квашеную  свеклу, свекольный рассол и вновь доводят до кипения. При подаче  в та', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 116, 'Литовская кухня', 'f59e0cee3678f3043cfd3f9fc8c6d15abb825da3.jpeg', 'Для бульона: кости 50, отвар грибной 75, свекла 60, лук репчатый 7, морковь 8, петрушка (Корень) 6, уксус (9%) 1,2, лавр, перец душистый, соль 2, для ушек: мука пшеничная 30, яйцо 1/4 шт., вода 10, для фарша: грибы сушеные 9, масло сливочное 3, лук репчат', 40, 'Борщ с ушками по-литовски', 'Варят костный бульон с луком и кореньями. За 40 минут до конца варки добавляют уксус, мелко нарезанную сырую свеклу, соль,  специи. Готовый бульон процеживают и соединяют с грибным  отваром. Замешивают тесто, раскатывают его тонким пластом,  нарезают квад', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 229, 'Литовская кухня', 'zrazy-s-yaicom-v-duxovke_1587365497_12_max.jpg', 'говядина (Задняя часть) - 500 г свиное сало - 100 г луковицы - 2 шт яйцо - 3 шт (2 вкрутую) ржаные толченых сухарей - пол стакана черный перец - 1 ч.л. майонез', 30, 'Зразы заливные', 'Говядину нарезать пластами толщиной 0,5 см, размером с ладонь или более, отбить (Обязательно), посолить, поперчить, смазать взбитым яйцом, выдержать 15 мин. Сало нарезать мелкими кубиками, вытопить до образования шкварок. Из обжаренного лука, яиц вкрутую,', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 134, 'Литовская кухня', '7dfc2a786e52b5c_660x440.jpg', 'сырых картофелин - 15 шт вареных карофелин - 2 шт гречневой муки - 6 ст. л. дрожжей - 25 г. воды - 2 ст. л. свиного сала - 100 г луковицы - 3 шт.', 35, 'Рагоуляй', '1. Из вареного картофеля сделать пюре, добавить дрожжи, разве - денные теплой водой, муку, поставить в теплое место на закваску.          2. Сырой картофель натереть, сок отжать, добавить муку (4 ст.  ложки) и закваску (П.1),поставить на 8 ч в теплое мест', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 386, 'Кухня Центральной Америки', '1323625003_orehi-s-kokosom-zapechennye-s-garam-masaloy.jpg', 'Орехи кокосовые 250, молоко 750, крахмал 20, сахар 20, яйца 2шт., коньяк 20, изюм 30, корица 2, ваниль.', 40, 'Кокосовые орехи запеченные (Коста Рика)', 'Картофельную муку разводят на 100 г холодного молока. Остальное молоко кипятят, добавляют в него разведенный крахмал и,  постоянно помешивая, доводят до кипения. Затем осторожно  добавляют измельченную на терке мякоть кокосовых орехов, изюм,  сахар, ванил', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 294, 'Кухня Центральной Америки', 'u19XgSgGpHA.jpg', 'Бананы 100, мука 10, молоко 30, масло сливочное 10, пудра сахарная 10, яйцо 1шт., соль.', 10, 'Лодочки из бананов (Гавайские острова)', 'Мякоть бананов растирают в пюре. Готовят белый соус из муки и сливочного масла, разбавляют его молоком, смешанным с сахарной  пудрой и солью. Соус снимают с огня и мешают до тех пор, пока он  не остынет. Добавляют желток, пюре из мякоти бананов и в конце ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 172, 'Кухня Центральной Америки', '2e8698d1fa098cfc77fd98ba34859218_cropped_740x494.jpg', 'Молоко 200, сахар 50, апельсин 1/2 шт., бананы 50.', 20, 'Напиток из бананов (Гаити)', 'Молоко кипятят с сахаром, охлаждают и добавляют сок апельсина. Жидкость вливают в банановое пюре, размешивают, охлаждают.  Напиток подают, надев на край стакана ломтик апельсина. Пьют его  через соломку.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 205, 'Кухня Центральной Америки', 'bcb95cc2.jpg', 'Ананас 80, яйца 2шт., пудра сахарная 10, ром 100, молоко 10, крахмал 3, ванилин.', 10, 'Омлет с ананасом (Ямайка)', 'Ананас очищают от кожуры, нарезают кубиками, посыпают сахарной пудрой и заливают ромом. Яичные желтки растирают добела с  сахарной пудрой, добавляют молоко, крахмал, порошок ванилин и в  конце - взбитые в пену белки. На сковороде разогревают сливочное  ма', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 149, 'Коми кухня', 'shanga-s-kartoshkoy.jpg', 'Для теста: мука 120, сахар 3, дрожжи 1, маргарин 5, вода 50, соль, картофель 150, молоко 20, сметана 20, яйцо 1/2 шт., масло сливочное 10.', 20, 'Картупеля нянь (Шаньги с картофелем)', 'В теплой воде или молоке растворяют сухие дрожжи, соль, сахар. Готовят дрожжевое тесто густой консистенции. Формуют шарики и  раскатывают их в лепешки. Отваренный в небольшом количестве воды  картофель тщательно разминают и, добавив горячее молоко, массу ', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 184, 'Карельская кухня', '00022518.jpg', 'Мука пшеничная 160, сахар 8, масло подсолнечное 8, дрожжи 3, яйцо 1/4 шт., лук репчатый 35, грибы соленые 150.', 40, 'Кулебяка с грибами', 'Кислое тесто раскатывают в полоску шириной 18-20 см и толщиной 1 см. На середи у полосы укладывают фарш из соленых рубленых  грибов и лука. Края теста соединяют и защипывают. Кулебяку  помещают на лист, смазывают яйцом и выпекают.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 109, 'Карельская кухня', 'recept-mussa-iz-revenya-s-mankoy-1024x680.jpg', 'Крупа манная 100, вода 700, сахар 175, ревень 350.', 20, 'Мусс манный с ревенем', 'Ревень очищают от волокон, промывают, мелко нарезают, 5 минут варят в воде с сахаром, протирают, добавляют творог и  перемешивают, а затем доводят до кипения. Всыпают манную крупу и  варят до загустения. Охладив до 40 град, массу взбивают в пену,  разлива', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 113, 'Карельская кухня', '2gis_ru_opt.jpeg', 'Окунь сушеный 80, картофель 200, мука 3, специи, сметана 10.', 30, 'Напарокко (Суп с сушеным окунем)', 'В кипящую подсоленную воду кладут ошпаренную сушеную рыбу и варят до готовности. Мякоть отделяют и закладывают в процеженный  бульон. Доведя до кипения, добавляют картофель, нарезанный  брусочками, специи, соль и продолжают варку. Незадолго до  готовности', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 206, 'Казахская кухня', 'kuyrdak-основное-фото-рецепта.jpg', 'Легкое 170, печень (Говяжья) 110, сердце 60, сало курдючное 90, лук репчатый 40, перец 0,5, соль.', 20, 'Куырдак (Поджарка казахская)', 'Нашинкованный репчатый лук и субпродукты, нарезанные мелкими кусочками, обжаривают с курдючным салом. Добавляют немного  бульона, соль, перец и тушат до готовности. Куырдак можно  готовить из говядины, баранины или конины.', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 174, 'Казахская кухня', 'b3622499.jpg', 'Мука 75, баранина 180, лук 60, сало топленое 5, уксус (3%) 25, перец молотый красный, соль.', 30, 'Манты с бараниной ', 'Жирную баранину нарезают мелкими кубиками и соединяют с рубленым репчатым уком, холодной водой, солью, перцем. Крутое пресное  тесто раскатывают на тонкие круглые лепешки весом по 20 г так,  чтобы края их были тоньше середины. На лепешку кладут фарш, края', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 113, 'Итальянская кухня ', 'https___img.anews.com_media_posts_images_20170313_64997428.jpg', 'Рис 125, лук репчатый 30, масло сливочное 25, сыр тертый 75, шампиньоны 25, бульон 125, помидоры 125, перец, соль.', 35, 'Ризотто по-милански ', 'Мелко нашинкованный лук слегка пассируют. Рис промывают, подсушивают и добавляют в лук. Прогревают, помешивая, пока рис  не станет прозрачным. Вливают бульон, заправляют солью и перцем  и тушат до готовности. Половину требуемого по рецепту количества  тер', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 119, 'Итальянская кухня ', 'rizotto_s_telyachim_yazykom_foto.jpg', 'Рис 80, лук репчатый 30, масло сливочное 20, тертый сыр 15, бульон 100, шафран, перец, соль.', 40, 'Ризотто по-пьемонтски ', 'Рубленый лук слегка обжаривают в сливочном масле, добавляют рис и жарят до тех пор, пока крупа не пропитается сливочным маслом.  Вливают частями бульон (Бульона должно быть по объему в два раза  больше, чем риса), добавляют шафран, соль, перец, перемешива', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 239, 'Итальянская кухня ', '76b7ed1fb420273f6dc238b8c29421e93d79cfe9.jpg', 'Цыпленок 80, сельдерей 30, грибы 30, каперсы 5, сыр тертый 10, майонез 40, помидоры свежие 20, соль.', 20, 'Салат "Тоска"', 'Нарезанные соломкой филе отварного цыпленка, сельдерей, свежие отваренные грибы соединяют с каперсами и тертым сыром,  заправляют солью и майонезом и перемешивают. Готовый салат  украшают помидорами.', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 133, 'Итальянская кухня ', 'salat_quotfigaroquot-409992.jpg', 'Язык телячий 50, свекла 40, сельдерей 40, салат 20, анчоусы 10, помидоры свежие 40, майонез 50, соль.', 15, 'Салат "Фигаро"', 'Вареный телячий язык, припущенный сельдерей, анчоусы и салат нарезают соломкой, вареную свеклу - кружочками. Все хорошо  перемешивают и заправляют майонезом и солью. В майонез  предварительно добавляют мелко нарубленные, без кожицы и семян,  свежие помидо', 'Салаты');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 218, 'Итальянская кухня', 'kurinye-nojki-v-temnoi-glazuri.jpg', '2стол. ложки подсолнечного масла 1 луковица среднего размера 2 зубчка чеснока 100 мп красного вина 500 г. протертых помидоров 100 г. консервированных маслин (без косточек), свежий шалфей', 25, 'Куриные окорочка а-ля Тоскана', 'Лук нарезать тонкими полукольцами. Чеснок очистить и пропустить через чесночницу. К окорочкам добавить лук и чеснок и быстро  обжарить.  Положить протертые помидоры. Накрыть сковороду крышкой и тушить  около 15 мин. Незадолго до готовности добавить маслин', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 265, 'Итальянская кухня', 'p_O_7.jpg', '1 пучок кореньев для супа 1л воды, соль, 200 г. грецких орехов, 2 луковицы 3 зубчика чеснока, 3 стол. ложки лимонного сока 125 г йогурта, 2 стол. ложки оливкового масла 0,5 чайн. ложки паприки, 2 стол. ложек масла из грецких орехов, мята', 40, 'Куриные ломтики в турецком соусе из грецких орехов', '1. Положить очищенные и мелко нарезанные коренья в подсоленную воду и довести ее до кипения. В течение 10 мин на небольшом огне  варить в ней до готовности куриное филе. Достать филе из бульо- на,  остудить и нарезать толстыми одинаковыми полосками.   Чес', 'Вторые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 89, 'Индийская кухня', 'unnamed0.jpg', 'Баранина 120, лук 20, петрушка, морковь, сельдерей (Корень), жир 8, лавр, кар и 2, лимон 1/6 шт., сметана 20, соль.', 120, 'Суп "Карри"', 'Мелко нашинкованный лук обжаривают в жире до золотистого цвета, добавляют карри и жарят еще 3 мин. Нашинкованные овощи жарят  вместе с кусочками мяса, кладут лавр, соль, вливают воду. Варят  на слабом огне, пока вода не выкипит наполовину. После этого  мя', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 115, 'Индийская кухня', 'karri-sup-iz-kuricy-s-risom.jpg', 'Цыпленок 70, лук 20, масло сливочное 5, перец стручковый 2, имбирь, кориандр, перец, соль.', 80, 'Суп куриный по-индийски', 'Тушку цыпленка мелко рубят, заливают водой, немного солят и варят, по мере необходимости добавляя воду. Хорошо растирают  острый стручковый перец, кориандр, черный перец, имбирь и  разводят все это небольшим количеством воды до густой кашицы.  Лук мелко н', 'Первые блюда');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 27, 'Русская кухня', 'domashniy-kvas.jpg', 'Хлеб ржаной  — 1 Килограмм , Дрожжи  — 15-20 Грамм (или 1/2 чайной ложки сухих), Сахар  — 1 Стакан (по вкусу, можно и больше), Вода  — 6 Литров', 3, 'Квас', 'нарезанный ломтиками хлеб подсушить, залить кипятком добавить сахар и оставить остывать до 30ти градусов. Кдрожжам добавить сахар и залить в кастрюлю с хлебом. Оставить бродить на пару дней. Процедить через льняное полотенце от хлеба и его остатков .Жидко', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 83, 'Французская кухня', '-204204382.jpg', 'Клубники  — 300 Грамм, Молока  — 1,5 Стакана, Мороженого пломбир  — 150 Грамм, Сахара  — 2 Ст. ложки ', 5, 'Молочный коктель с клубникой', 'Клубнику с сахаром измельчить, добавить мягкое мороженное и влить молоко. Взбить коктель до появления пенки и подать', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 76, 'Итальянская кухня ', 'image003-181.jpg', 'Смородина черная  — 3 Килограмма,  Вода  — 2 Литра,  Сахар  — 500 Грамм', 180, 'Вино из черной смородины', 'Перебрать смородину, разомнуть любым способом и добавить литр воды, перемешать и оставить на 4 суток, переодически помешивая. Пото процедить житкость от ягод, перелить в чистые бутылки и добавить сахар. Убрать в сухое место на полтора месяца. Готовое вино', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 94, 'Русская кухня', 'Bez-nazvanija-5-11.jpg', 'Вода  — 1,5 Литра, Тархун  — 5 Грамм (лист), Мята  — 10 Грамм (лист), Сахар  — 120 Грамм, Лимон  — 1 Штука', 60, 'Лимонад из тархуна, лимона и мяты', 'Листья тархуна засыпать ст. ложкой сахара и разомнуть. С лимона снять цедру и выдавить сок. Залить водой цедру, листья тархуна и мяты, добавить весь сахар и варить 5 минут. Остудить, процедить и можно подавать.', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 52, 'Русская кухня', '40ff48435cc1b6d0f75f7fd45ad79967.jpg', 'Сахар  — 600 Грамм, Черная смородина  — 1 Килограмм', 40, 'Компот из черной смородины', 'Залить ягоды кипятком, дать остыть 5 минут, а потом снова прокипятить и разлить по банкам, которые сразу же закать и хранить в прохладном месте.', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 43, 'Русская кухня', '2821635.jpg', 'Киви  — 5-6 Штук, Мандарин  — 4 Штуки, Яблоко  — 1 Штука, Лимонная кислота  — 1/2 Чайных ложки (или лимонный сок по вкусу), Сахар  — 4-5 Ст. ложек ', 30, 'Компот из киви', 'Фрукты поместить в дуршлаг и прокипятить их 2-3 минуты. В отвар добавить сахар и варить 5 минут. В банки добавить фрукты и залить жидкостю, можно употреблять. ', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 96, 'Итальянская кухня ', 'sangria.jpg', 'Вино красное п/сладкое  — 400 Миллилитров, Яблоки  — 3 Штуки, Клубника  — 15 Штук (свежемороженая), Апельсин  — 2 Штуки, Мед (или сахар)  — 1/2 Стакана (по вкусу), Лед  — 100 Грамм', 5, 'Сангрия', 'Из апельсинов надо выжать сок. Яблоки очистите и нарежьте дольками. Соедините все ингредиенты в чаше измельчителя. Пробейте до однородности. Можно подавать', 'Напитки');
INSERT INTO public.recipe (accepted, calories, cuisine, image, ingredients, minutes, name, recipe, type) VALUES (true, 106, 'Английская кухня', 'maxresdefault.jpg', 'Сливки  — 30 Миллилитров, Корица  — 0,5 Чайных ложки, Мороженое  — 70-100 Грамм, Газировка  — 1/2 Стакана, Ириски  — 9 Штук ', 15, 'Сливочное пиво из "Гарри Поттера"', 'В сотейник вылить сливки, добавить ириски и корицу. На маленьком огне растопить ириски и довести массу до густой консистенции. После жэтого охладить сливки и смешать с мородженным в блендере, Половину стакана заполните сливками, а вторую половину - газиро', 'Напитки');